import React, { useState, useCallback, useMemo } from 'react';
import './App.css';
import DemoList from './components/Demo/DemoList';
import Button from './components/UI/Button/Button';

function App() {
  const [listTitle, setListTitle] = useState('My List');
  const [ascendingOrder, setAscendingOrder] = useState(true); // New state for sorting order

  const changeTitleHandler = useCallback(() => {
    setListTitle('New Title');
  }, []);

  const toggleOrderHandler = useCallback(() => {
    setAscendingOrder((prevOrder) => !prevOrder);
  }, []);

  const listItems = useMemo(() => [5, 3, 1, 10, 9], []);

  const sortedList = useMemo(() => {
    console.log('Items sorted');
    return [...listItems].sort((a, b) => (ascendingOrder ? a - b : b - a));
  }, [listItems, ascendingOrder]);

  return (
    <div className="app">
      <DemoList title={listTitle} items={sortedList} /> {/* Pass sortedList to DemoList */}
      <Button onClick={changeTitleHandler}>Change List Title</Button>
      <Button onClick={toggleOrderHandler}>Change to Descending Order</Button>
    </div>
  );
}

export default App;
